package A;

public class Array1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr=new int[] {10,20,30};
		for(int i=0;i<arr.length;i++) {
			System.out.println(arr[i]);
		}
		for(int i=0;i<arr.length;i++) {
			System.out.print(arr[i]+" ");
		}
	}

}
